import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authenticateUser from "./authenticateUser";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [error, setError] = useState("");
  const [isChangingPassword, setIsChangingPassword] = useState(false);

  const Navigate = useHistory();

  const handleSignIn = async (event) => {
    event.preventDefault();
    setError(""); // Clear any existing error messages

    try {
      const response = await authenticateUser(username, password, newPassword);

      if (response.status === "newPasswordRequired") {
        // Save the user object and ask for a new password
        setIsChangingPassword(true);
      } else if (response.status === "success") {
        console.log("Login successful!", response.data);
        history.push("/success"); // Redirect on successful login
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleNewPasswordSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await authenticateUser(username, password, newPassword);
      if (response.status === "success") {
        console.log("Password change and login successful!", response.data);
        history.push("/success"); // Redirect on successful password change and login
      }
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div>
      {!isChangingPassword ? (
        // Regular login form
        <form onSubmit={handleSignIn}>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
          />
          <button type="submit">Login</button>
        </form>
      ) : (
        // New password form
        <form onSubmit={handleNewPasswordSubmit}>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            placeholder="New Password"
          />
          <button type="submit">Set New Password</button>
        </form>
      )}
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default Login;
