import {
  CognitoUserPool,
  CognitoUserAttribute,
} from "amazon-cognito-identity-js";

const poolData = {
  UserPoolId: "YOUR_COGNITO_USER_POOL_ID", // Replace with your Cognito User Pool ID
  ClientId: "YOUR_COGNITO_APP_CLIENT_ID", // Replace with your Cognito App Client ID
};

const userPool = new CognitoUserPool(poolData);

export const registerUser = async (username, password) => {
  // Define user attributes
  // Assuming 'email' is used as the username, and it's also an attribute
  const attributeList = [
    new CognitoUserAttribute({
      Name: "email",
      Value: username,
    }),
  ];

  // Return a promise that resolves on successful registration
  return new Promise((resolve, reject) => {
    userPool.signUp(username, password, attributeList, null, (err, result) => {
      if (err) {
        reject(err);
      } else {
        resolve(result);
      }
    });
  });
};
