import React, { useState, useRef } from "react";
import { jsxDEV as _jsxDEV } from "react/jsx-dev-runtime";
const SuccessPage = () => {
  _s();
  const initialFormData = {
    FullName: "",
    RequiredID: "",
    RequiredTitle: "",
    Role: "",
    RequiredCreationDate: "",
    VendorID: "",
    ImmigrationStatus: "",
    ContractType: "",
    SubmissionDate: "",
    RequiredSubmissionEndDate: "",
    CandidateCurrentLocation: "",
    ContactNumber: "",
    RecruiterName: "",
    State: "",
    SubmissionStatus: "",
    VendorRate: "",
    CandidatePayRate: "",
    BillRateMargin: "",
    ResumeSource: "",
    EmailId: "",
    LinkedInID: "",
    EmployerInformation: "",
    ProfessionalReferences: "",
    DOB: "",
    RequiredSkills: "",
    ResumeFormatingNeeded: "",
    FormatedBy: "",
    Resume: null
  };
  const [formData, setFormData] = useState(initialFormData);
  const [validationErrors, setValidationErrors] = useState({});
  const [showPopup, setShowPopup] = useState(false);
  const resumeInputRef = useRef(null); // Create a ref for the resume input

  const isValidEmail = email => {
    return /\S+@\S+\.\S+/.test(email);
  };
  const handleInputChange = e => {
    const {
      id,
      value,
      files
    } = e.target;
    if (id === "Resume" && files) {
      const file = files[0];
      if (file) {
        const allowedTypes = ["application/pdf", "application/msword", "application/vnd.openxmlformats-officedocument.wordprocessingml.document"];
        if (!allowedTypes.includes(file.type) || file.size > 1024 * 1024 * 10) {
          setValidationErrors({
            ...validationErrors,
            Resume: "Only PDF or DOC files under 10MB are allowed."
          });
        } else {
          setFormData({
            ...formData,
            Resume: file
          });
          let newErrors = {
            ...validationErrors
          };
          delete newErrors.Resume;
          setValidationErrors(newErrors);
        }
      }
    } else {
      setFormData({
        ...formData,
        [id]: value
      });
      let newErrors = {
        ...validationErrors
      };
      delete newErrors[id];
      setValidationErrors(newErrors);
    }
  };
  const validateForm = () => {
    let isValid = true;
    const newErrors = {};
    Object.keys(formData).forEach(key => {
      if (!formData[key] && key !== "Resume") {
        newErrors[key] = "This field is required.";
        isValid = false;
      }
    });
    if (formData.EmailId && !isValidEmail(formData.EmailId)) {
      newErrors.EmailId = "Invalid email format.";
      isValid = false;
    }
    if (!formData.Resume) {
      newErrors.Resume = "Please upload a resume.";
      isValid = false;
    }
    setValidationErrors(newErrors);
