import React, { useState, useRef } from "react";

function SuccessPage() {
  const initialFormData = {
    FullName: "",
    ReqId: "",
    EmailId: "",
    // Additional form fields
    ResumeUrl: "", // This will hold the URL of the uploaded resume
  };

  const [formData, setFormData] = useState(initialFormData);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showPopup, setShowPopup] = useState(false);
  const resumeInputRef = useRef(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData({ ...formData, Resume: file });
    }
  };

  const uploadResume = async (file) => {
    const filename = encodeURIComponent(file.name);
    const uploadUrl = `https://lrl0r0t06c.execute-api.us-east-1.amazonaws.com/Prod/upload/${filename}`;
    const fileData = new FormData();
    fileData.append("file", file);

    const response = await fetch(uploadUrl, {
      method: "POST",
      body: fileData,
    });

    if (!response.ok) {
      throw new Error("Failed to upload file.");
    }

    const data = await response.json();
    return data.url; // Assuming the response contains the URL of the uploaded file
  };

  const submitFormDataToDynamoDB = async (data) => {
    const apiEndpoint = "https://your-api-endpoint-for-dynamodb.com/submit";
    const response = await fetch(apiEndpoint, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    });

    if (!response.ok) {
      throw new Error("Failed to submit form data.");
    }

    return await response.json(); // Assuming the API returns some response
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      if (formData.Resume) {
        const resumeUrl = await uploadResume(formData.Resume);
        const updatedFormData = { ...formData, ResumeUrl: resumeUrl };
        delete updatedFormData.Resume; // Remove the file object

        await submitFormDataToDynamoDB(updatedFormData);
        console.log("Form and resume submitted successfully");
        setShowPopup(true);
        setFormData(initialFormData); // Reset form
      } else {
        throw new Error("Resume file is required.");
      }
    } catch (error) {
      console.error("Submission error:", error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div>
      <h2>Submit Form and Resume</h2>
      <form onSubmit={handleSubmit}>
        {/* Form fields */}
        <input
          type="text"
          name="FullName"
          value={formData.FullName}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="EmailId"
          value={formData.EmailId}
          onChange={handleInputChange}
        />
        {/* Include other fields as needed */}
        <input type="file" onChange={handleFileChange} ref={resumeInputRef} />
        <button type="submit" disabled={isSubmitting}>
          Submit
        </button>
      </form>

      {showPopup && (
        <div>
          <p>Your form has been submitted successfully.</p>
          <button onClick={() => setShowPopup(false)}>Close</button>
        </div>
      )}
    </div>
  );
}

export default SuccessPage;
