import React, { useState, useRef } from "react";

function SuccessPage() {
  const initialFormData = {
    FullName: "",
    RequiredID: "",
    RequiredTitle: "",
    Role: "",
    RequiredCreationDate: "",
    VendorID: "",
    ImmigrationStatus: "",
    ContractType: "",
    SubmissionDate: "",
    RequiredSubmissionEndDate: "",
    CandidateCurrentLocation: "",
    ContactNumber: "",
    RecruiterName: "",
    State: "",
    SubmissionStatus: "",
    VendorRate: "",
    CandidatePayRate: "",
    BillRateMargin: "",
    ResumeSource: "",
    EmailId: "",
    LinkedInID: "",
    EmployerInformation: "",
    ProfessionalReferences: "",
    DOB: "",
    RequiredSkills: "",
    ResumeFormattingNeeded: "",
    FormattedBy: "",
    Resume: null, // Placeholder for the resume file
  };

  const [formData, setFormData] = useState(initialFormData);
  const [validationErrors, setValidationErrors] = useState({});
  const [showPopup, setShowPopup] = useState(false);
  const resumeInputRef = useRef(null);

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    setValidationErrors((prevErrors) => ({ ...prevErrors, [name]: null }));

    if (name === "Resume" && files) {
      setFormData((prevFormData) => ({ ...prevFormData, Resume: files[0] }));
    } else {
      setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
    }
  };

  const validateForm = () => {
    let isValid = true;
    const newErrors = {};
    Object.entries(formData).forEach(([key, value]) => {
      if (!value && key !== "Resume") {
        newErrors[key] = "This field is required";
        isValid = false;
      }
    });

    const uploadResume = async (file) => {
      const filename = encodeURIComponent(file.name);
      const uploadUrl = `https://lrl0r0t06c.execute-api.us-east-1.amazonaws.com/Prod/upload/${filename}`;
      const fileData = new FormData();
      fileData.append("file", file);

      try {
        const response = await fetch(uploadUrl, {
          method: "POST",
          body: fileData,
        });
        if (!response.ok) throw new Error("Failed to upload resume");
        return await response.json();
      } catch (error) {
        console.error("Upload Error:", error);
        alert("Upload failed: " + error.message);
        return null;
      }
    };

    const submitFormDetails = async (formData) => {
      const submitUrl =
        "https://8ofx3c8vk0.execute-api.us-east-1.amazonaws.com/Prod/submit";
      try {
        const response = await fetch(submitUrl, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(formData),
        });
        if (!response.ok) throw new Error("Failed to submit form details");
        return await response.json();
      } catch (error) {
        console.error("Submission Error:", error);
        alert("Submission failed: " + error.message);
        return null;
      }
    };

    // const handleSubmit = async (e) => {
    //   e.preventDefault();
    //   if (formData.Resume) {
    //     const uploadResult = await uploadResume(formData.Resume);
    //     if (uploadResult && uploadResult.url) {
    //       const formSubmissionResponse = await submitFormDetails({
    //         ...formData,
    //         ResumeUrl: uploadResult.url,
    //       });
    //       if (formSubmissionResponse) {
    //         console.log("Form submitted successfully");
    //         setShowPopup(true);
    //       }
    //     }
    //   } else {
    //     console.log("Please select a resume to upload.");
    //   }
    // };

    const handleSubmit = async (e) => {
      e.preventDefault();

      if (!validateForm()) {
        console.log("Form validation failed", validationErrors);
        return;
      }

      if (formData.Resume) {
        try {
          const uploadResult = await uploadResume(formData.Resume);
          if (uploadResult && uploadResult.url) {
            // Assuming the resume upload was successful, update formData with the resume URL
            const updatedFormData = {
              ...formData,
              ResumeUrl: uploadResult.url,
            };

            console.log("Form Data:", JSON.stringify(updatedFormData, null, 2));
            setShowPopup(true);
          } else {
            console.log("Failed to upload resume.");
          }
        } catch (error) {
          alert(error.message);
        }
      } else {
        console.log("Please select a resume to upload.");
      }
    };

    const handleClosePopup = () => {
      setShowPopup(false);
      setFormData(initialFormData);
      setValidationErrors({});
      if (resumeInputRef.current) {
        resumeInputRef.current.value = "";
      }
    };

    return (
      <div>
        {/* The form component with Tailwind CSS */}
        <section className="max-w-4xl p-6 mx-auto bg-indigo-600 rounded-md shadow-md dark:bg-gray-800 mt-20">
          <h1 className="text-xl font-bold text-white capitalize dark:text-white">
            Employee Information Form
          </h1>
          <form onSubmit={handleSubmit}>
            <div className="grid grid-cols-1 gap-6 mt-4 sm:grid-cols-2">
              {/* Input fields generated based on formData */}
              {Object.keys(initialFormData)
                .filter((key) => key !== "Resume")
                .map((key) => (
                  <div key={key} className="mb-4">
                    <label
                      htmlFor={key}
                      className="text-white dark:text-gray-200 block text-sm font-bold mb-2"
                    >
                      {key.replace(/([A-Z])/g, " $1").trim()}
                    </label>
                    <input
                      type="text"
                      id={key}
                      name={key}
                      value={formData[key]}
                      onChange={handleInputChange}
                      className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
                    />
                    {validationErrors[key] && (
                      <p className="text-red-500 text-xs italic">
                        {validationErrors[key]}
                      </p>
                    )}
                  </div>
                ))}
              <div className="mb-4">
                <label
                  htmlFor="Resume"
                  className="block text-sm font-medium text-white"
                >
                  Resume
                </label>
                <input
                  id="Resume"
                  name="Resume"
                  type="file"
                  onChange={handleInputChange}
                  ref={resumeInputRef}
                  className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-violet-50 file:text-violet-700 hover:file:bg-violet-100"
                />
                {validationErrors["Resume"] && (
                  <p className="text-red-500 text-xs italic">
                    {validationErrors["Resume"]}
                  </p>
                )}
              </div>
              <button
                type="submit"
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              >
                Submit
              </button>
            </div>
          </form>
        </section>
        {showPopup && (
          <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex justify-center items-center">
            <div className="bg-white p-5 rounded-lg shadow-lg">
              <h2 className="text-lg mb-4">Submission Successful</h2>
              <p>Your form has been submitted successfully.</p>
              <button
                onClick={handleClosePopup}
                className="mt-3 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    );
  };
}

export default SuccessPage;
