import {
  CognitoUserPool,
  CognitoUserAttribute,
} from "amazon-cognito-identity-js";

// Replace these with your Cognito User Pool details
const poolData = {
  UserPoolId: "us-east-2_3Ms1eFrei",
  ClientId: "12gd95t82qp31uf8a5vgo0fkmb",
};

const userPool = new CognitoUserPool(poolData);

export const registerUser = async (username, password) => {
  const attributeList = [];

  // Example: Adding email as an attribute
  const emailAttribute = {
    Name: "email",
    Value: username, // Assuming the username is the email
  };
  attributeList.push(new CognitoUserAttribute(emailAttribute));

  return new Promise((resolve, reject) => {
    userPool.signUp(username, password, attributeList, null, (err, result) => {
      if (err) {
        reject(err);
      } else {
        resolve(result);
      }
    });
  });
};
