import { AuthenticationDetails, CognitoUser } from "amazon-cognito-identity-js";
import UserPool from "./CognitoConfig";

const authenticateUser = (username, password, newPassword) => {
  return new Promise((resolve, reject) => {
    const user = new CognitoUser({ Username: username, Pool: UserPool });
    const authDetails = new AuthenticationDetails({
      Username: username,
      Password: password,
    });

    user.authenticateUser(authDetails, {
      onSuccess: (data) => resolve({ status: "success", data }),
      onFailure: (err) => reject(err),
      newPasswordRequired: (userAttributes, requiredAttributes) => {
        // User was signed up by an admin and must provide a new password and required attributes
        // If newPassword is not provided, return a specific object to indicate the need for a new password
        if (!newPassword) {
          resolve({
            status: "newPasswordRequired",
            user,
            userAttributes,
            requiredAttributes,
          });
        } else {
          // Here you can remove attributes you don't want to send back
          delete userAttributes.email_verified; // example attribute

          // Set the new password for the user
          user.completeNewPasswordChallenge(newPassword, userAttributes, this);
        }
      },
    });
  });
};

export default authenticateUser;
