// 6 - Working

import React, { useState, useRef } from "react";

function SuccessPage() {
  const initialFormData = {
    FullName: "",
    RequiredID: "",
    RequiredTitle: "",
    Role: "",
    RequiredCreationDate: "",
    VendorID: "",
    ImmigrationStatus: "",
    ContractType: "",
    SubmissionDate: "",
    RequiredSubmissionEndDate: "",
    CandidateCurrentLocation: "",
    ContactNumber: "",
    RecruiterName: "",
    State: "",
    SubmissionStatus: "",
    VendorRate: "",
    CandidatePayRate: "",
    BillRateMargin: "",
    ResumeSource: "",
    EmailId: "",
    LinkedInID: "",
    EmployerInformation: "",
    ProfessionalReferences: "",
    DOB: "",
    RequiredSkills: "",
    ResumeFormattingNeeded: "",
    FormattedBy: "",
    Resume: null,
  };

  const [formData, setFormData] = useState(initialFormData);
  const [showPopup, setShowPopup] = useState(false);
  const resumeInputRef = useRef(null); // Reference for the file input

  // Update form state on user input
  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "Resume" && files) {
      setFormData({ ...formData, Resume: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  // Upload resume to S3 and return the file URL
  const uploadResume = async (file) => {
    const filename = encodeURIComponent(file.name);
    const uploadUrl = `https://lrl0r0t06c.execute-api.us-east-1.amazonaws.com/Prod/upload/${filename}`;
    const fileData = new FormData();
    fileData.append("file", file);

    try {
      const response = await fetch(uploadUrl, {
        method: "POST",
        body: fileData,
      });
      if (!response.ok) throw new Error("Failed to upload resume");
      return await response.json();
    } catch (error) {
      console.error("Upload Error:", error);
      alert("Upload failed: " + error.message);
      return null;
    }
  };

  // Submit form details including resume URL to DynamoDB
  const submitFormDetails = async (formData) => {
    const submitUrl =
      "https://8ofx3c8vk0.execute-api.us-east-1.amazonaws.com/Prod/submit";
    try {
      const response = await fetch(submitUrl, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      if (!response.ok) throw new Error("Failed to submit form details");
      return await response.json();
    } catch (error) {
      console.error("Submission Error:", error);
      alert("Submission failed: " + error.message);
      return null;
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.Resume) {
      const uploadResult = await uploadResume(formData.Resume);
      if (uploadResult && uploadResult.url) {
        // Assume uploadResult contains the URL of the uploaded file
        const formSubmissionResponse = await submitFormDetails({
          ...formData,
          ResumeUrl: uploadResult.url,
        });
        if (formSubmissionResponse) {
          console.log("Form submitted successfully");
          setShowPopup(true);
        }
      }
    } else {
      console.log("Please select a resume to upload.");
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {/* Iterate over the formData keys to generate input fields */}
        {Object.keys(initialFormData).map((key) => {
          if (key === "Resume") {
            return (
              <div key={key}>
                <label>{key}:</label>
                <input
                  type="file"
                  name={key}
                  onChange={handleInputChange}
                  ref={resumeInputRef}
                  required
                />
              </div>
            );
          } else {
            return (
              <div key={key}>
                <label>{key}:</label>
                <input
                  type="text"
                  name={key}
                  value={formData[key]}
                  onChange={handleInputChange}
                  required
                />
              </div>
            );
          }
        })}
        <button type="submit">Submit</button>
      </form>
      {showPopup && (
        <div className="popup">
          <p>Form submitted successfully!</p>
          <button onClick={() => setShowPopup(false)}>Close</button>
        </div>
      )}
    </div>
  );
}

