  const uploadResume = async (file) => {
    const filename = `${formData.ReqId}_${formData.FullName}_${file.name}`;
    const uploadUrl = `${process.env.REACT_APP_API_URL}/${filename}`;

    const fileData = new FormData();
    console.log("Uploading file:", file.name, file.size, file.type);

    // Create a new FileReader to read this file into memory
    const reader = new FileReader();

    // Define the event handler that is fired when the reader has finished loading the file
    reader.onload = async (e) => {
      // e.target.result contains the file's data as a base64 encoded string
      console.log("File Data Loaded:", e.target.result);

      // Append the file data to FormData
      fileData.append("file", file, filename);
      console.log("Before File Data:", fileData);

      try {
        const response = await fetch(uploadUrl, {
          method: "POST",
          body: fileData,
        });

        console.log("After File Data:", fileData);
        const data = await response.json();

        console.log("Server response:", data);

        if (!response.ok) {
          throw new Error(`Server responded with status: ${response.status}`);
        }

        if (data.message === "File uploaded successfully") {
          console.log(data.message);
          return "Upload successful, no URL provided";
        } else {
          throw new Error(
            "Unexpected server response, upload may not be successful."
          );
        }
      } catch (error) {
        console.error("Upload Error:", error);
        throw error;
      }
    };

    // Define the event handler that is fired if the file read fails
    reader.onerror = (error) => {
      console.error("Error reading file:", error);
      throw error;
    };

    // Read the file as a Data URL (base64 encoded string of the file's data)
    reader.readAsDataURL(file);
  };

