import React, { useState } from "react";

const SuccessPage = () => {
  const [formData, setFormData] = useState({
    FullName: "",
    RequiredID: "",
    RequiredTitle: "",
    Role: "",
    RequiredCreationDate: "",
    VendorID: "",
    ImmigrationStatus: "",
    ContractType: "",
    SubmissionDate: "",
    RequiredSubmissionEndDate: "",
    CandidateCurrentLocation: "",
    ContactNumber: "",
    RecruiterName: "",
    State: "",
    SubmissionStatus: "",
    VendorRate: "",
    CandidatePayRate: "",
    BillRateMargin: "",
    ResumeSource: "",
    EmailId: "",
    LinkedInID: "",
    EmployerInformation: "",
    ProfessionalReferences: "",
    DOB: "",
    RequiredSkills: "",
    ResumeFormatingNeeded: "",
    FormatedBy: "",
    Password: "",
    PasswordConfirmation: "",
    Resume: null,
  });
  const [showPopup, setShowPopup] = useState(false);
  const [uploadError, setUploadError] = useState("");

  const handleInputChange = (e) => {
    const { id, value, files } = e.target;
    if (id === "Resume") {
      const file = files ? files[0] : null;
      if (file) {
        const allowedTypes = [
          "application/pdf",
          "application/msword",
          "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
        ];
        if (!allowedTypes.includes(file.type) || file.size > 10 * 1024 * 1024) {
          // Set error if file is not PDF/DOC or larger than 10MB
          setUploadError("Only PDF or DOC files under 10MB are allowed.");
          return;
        }
        // Update form data and clear any error if file is valid
        setFormData({ ...formData, Resume: file });
        setUploadError("");
      }
    } else {
      setFormData({ ...formData, [id]: value });
    }
  };

