import React, { useState, useRef } from "react";

function SuccessPage() {
  const initialFormData = {
    FullName: "",
    ReqId: "",
    ReqTitle: "",
    Role: "",
    ReqCreationDate: "",
    VendorID: "",
    BillRate: "",
    ImmigrationStatus: "",
    ContractType: "",
    SubmissionDate: "",
    ReqSubmissionEndDate: "",
    CandidateCurrentLocation: "",
    ContactNumber: "",
    RecruiterName: "",
    State: "",
    SubmissionStatus: "",
    VendorRate: "",
    CandidatePayRate: "",
    BillRateMargin: "",
    ResumeSource: "",
    EmailId: "",
    LinkedInID: "",
    EmployerInformation: "",
    ProfessionalReferences: "",
    DOB: "",
    ReqSkills: "",
    ResumeFormattingNeeded: "",
    FormattedBy: "",
    Resume: null,
  };

  const [formData, setFormData] = useState(initialFormData);
  const [validationErrors, setValidationErrors] = useState({});
  const [showPopup, setShowPopup] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const resumeInputRef = useRef(null);

  const isValidEmail = (email) => /\S+@\S+\.\S+/.test(email);

  const handleInputChange = (e) => {
    const { id, value, files } = e.target;
    if (id === "Resume" && files) {
      const file = files[0];
      if (!file) return;
      const allowedTypes = [
        "application/pdf",
        "application/msword",
        "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
      ];
      if (!allowedTypes.includes(file.type) || file.size > 1024 * 1024 * 10) {
        setValidationErrors((prevErrors) => ({
          ...prevErrors,
          Resume: "Only PDF or DOC files under 10MB are allowed.",
        }));
      } else {
        setFormData((prevFormData) => ({ ...prevFormData, Resume: file }));
        setValidationErrors((prevErrors) => {
          const newErrors = { ...prevErrors };
          delete newErrors.Resume;
          return newErrors;
        });
      }
    } else {
      setFormData((prevFormData) => ({ ...prevFormData, [id]: value }));
      setValidationErrors((prevErrors) => {
        const newErrors = { ...prevErrors };
        delete newErrors[id];
        return newErrors;
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};
    let isValid = true;

    Object.keys(formData).forEach((key) => {
      if (!formData[key] && key !== "Resume") {
        newErrors[key] = "This field is required.";
        isValid = false;
      }
    });

    if (formData.EmailId && !isValidEmail(formData.EmailId)) {
      newErrors.EmailId = "Invalid email format.";
      isValid = false;
    }

    if (!formData.Resume) {
      newErrors.Resume = "Please upload a resume.";
      isValid = false;
    }

    setValidationErrors(newErrors);
    return isValid;
  };

  const uploadResume = async (file) => {
    const filename = encodeURIComponent(file.name);
    const uploadUrl = `${process.env.REACT_APP_API_URL}/upload/${filename}`;
    const fileData = new FormData();
    fileData.append("file", file);

    try {
      const response = await fetch(uploadUrl, {
        method: "POST",
        body: fileData,
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(`Server responded with status: ${response.status}`);
      }
      console.log("Server response:", data.message);
      // Assuming the backend response contains the URL or path of the uploaded file
      return data.url || "Upload successful, URL not provided";
    } catch (error) {
      console.error("Upload Error:", error);
      throw error;
    }
  };

  const submitForm = async () => {
    const submitUrl =
      "https://mkou1u2f62.execute-api.us-east-1.amazonaws.com/Prod/submit";
    try {
      const response = await fetch(submitUrl, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      if (!response.ok) throw new Error("Failed to submit form.");
      const data = await response.json();
      return data;
    } catch (error) {
      console.error("Submission error:", error);
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    setIsSubmitting(true);

    try {
      let resumeUrl = "";
      if (formData.Resume) {
        resumeUrl = await uploadResume(formData.Resume);
      }
      const updatedFormData = { ...formData, ResumeUrl: resumeUrl };
      await submitForm(updatedFormData);
      setShowPopup(true);
      setFormData(initialFormData);
    } catch (error) {
      alert(`Error: ${error.message}`);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleClosePopup = () => {
    setShowPopup(false);
    setFormData(initialFormData);
    setValidationErrors({});
    if (resumeInputRef.current) {
      resumeInputRef.current.value = "";
    }
  };

  // Dynamically generate form fields
  const renderFormFields = () => {
    return Object.entries(initialFormData).map(([key, value]) => {
      if (key === "Resume") return null; // Skip rendering for Resume field here
      return (
        <div key={key}>
          <label htmlFor={key} className="text-white dark:text-gray-200">
            {key}
          </label>
          <input
            id={key}
            type={
              key.includes("Date") || key.includes("EmailId")
                ? "text"
                : "String"
            }
            placeholder={key}
            value={formData[key]}
            onChange={handleInputChange}
            className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
          />
          {validationErrors[key] && (
            <p className="text-red-500 text-xs italic">
              {validationErrors[key]}
            </p>
          )}
        </div>
      );
    });
  };

  return (
    <div>
      <section className="max-w-4xl p-6 mx-auto bg-indigo-600 rounded-md shadow-md dark:bg-gray-800 mt-20">
        <h1 className="text-xl font-bold text-white capitalize dark:text-white">
          Employee Information Form
        </h1>
        <form onSubmit={handleSubmit}>
          <div className="grid grid-cols-1 gap-6 mt-4 sm:grid-cols-2">
            {renderFormFields()}
            {/* Resume field */}
            <div>
              <label
                htmlFor="Resume"
                className="block text-sm font-medium text-white"
              >
                Resume
              </label>
              <input
                id="Resume"
                name="Resume"
                type="file"
                onChange={handleInputChange}
                ref={resumeInputRef}
                className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-violet-50 file:text-violet-700 hover:file:bg-violet-100"
              />
              {validationErrors["Resume"] && (
                <p className="text-red-500 text-xs italic">
                  {validationErrors["Resume"]}
                </p>
              )}
            </div>
            <button
              type="submit"
              disabled={isSubmitting}
              className="col-span-2 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Submit
            </button>
          </div>
        </form>
      </section>
      {showPopup && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex justify-center items-center">
          <div className="bg-white p-5 rounded-lg shadow-lg">
            <h2 className="text-lg mb-4">Submission Successful</h2>
            <p>Your form has been submitted successfully.</p>
            <button
              onClick={handleClosePopup}
              className="mt-3 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default SuccessPage;
