import React, { useState } from "react";

function UploadForm() {
  const [formData, setFormData] = useState({
    FullName: "",
    ReqId: "",
    EmailId: "",
  });
  const [resume, setResume] = useState(null);

  // Handles changes in text inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Handles resume file selection
  const handleResumeChange = (e) => {
    setResume(e.target.files[0]);
  };

  // Upload the resume to S3
  const uploadResume = async (file) => {
    const filename = encodeURIComponent(file.name);
    const uploadUrl = `https://lrl0r0t06c.execute-api.us-east-1.amazonaws.com/Prod/upload/${filename}`;
    const fileData = new FormData();
    fileData.append("file", file);

    try {
      const response = await fetch(uploadUrl, {
        method: "POST",
        body: fileData,
      });
      if (!response.ok) {
        throw new Error("Failed to upload resume");
      }
      const uploadResult = await response.json();
      return uploadResult; // Assuming this includes the URL of the uploaded file
    } catch (error) {
      console.error("Upload Error:", error);
      alert("Upload failed: ", error.message);
      return null;
    }
  };

  // Submit form details to DynamoDB
  const submitFormDetails = async (details, resumeUrl) => {
    const submitUrl =
      "https://8ofx3c8vk0.execute-api.us-east-1.amazonaws.com/Prod/submit";
    details.resumeUrl = resumeUrl; // Add the resume URL to form details

    try {
      const response = await fetch(submitUrl, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(details),
      });
      if (!response.ok) {
        throw new Error("Failed to submit form details");
      }
      return await response.json();
    } catch (error) {
      console.error("Submission Error:", error);
      alert("Submission failed: ", error.message);
      return null;
    }
  };

  // Handles the form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!resume) {
      alert("Please select a resume to upload.");
      return;
    }

    // First, upload the resume
    const uploadResult = await uploadResume(resume);
    if (uploadResult && uploadResult.fileUrl) {
      // Then, submit the form details along with the resume URL
      const submitResult = await submitFormDetails(
        formData,
        uploadResult.fileUrl
      );
      if (submitResult) {
        alert("Form and resume submitted successfully!");
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Full Name:</label>
        <input
          type="text"
          name="FullName"
          value={formData.FullName}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Req ID:</label>
        <input
          type="text"
          name="ReqId"
          value={formData.ReqId}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Email:</label>
        <input
          type="email"
          name="EmailId"
          value={formData.EmailId}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Resume:</label>
        <input
          type="file"
          name="resume"
          onChange={handleResumeChange}
          required
        />
      </div>
      <button type="submit">Submit</button>
    </form>
  );
}

export default UploadForm;
