import React, { useState, useRef } from "react";

function SuccessPage() {
  const initialFormData = {
    FullName: "",
    // RequiredID: "",
    ReqId: "",
    ReqTitle: "",
    Role: "",
    ReqCreationDate: "",
    VendorID: "",
    ImmigrationStatus: "",
    ContractType: "",
    SubmissionDate: "",
    ReqSubmissionEndDate: "",
    CandidateCurrentLocation: "",
    ContactNumber: "",
    RecruiterName: "",
    State: "",
    SubmissionStatus: "",
    VendorRate: "",
    CandidatePayRate: "",
    BillRateMargin: "",
    BillRate: "",
    ResumeSource: "",
    EmailId: "",
    LinkedInID: "",
    EmployerInformation: "",
    ProfessionalReferences: "",
    DOB: "",
    ReqSkills: "",
    ResumeFormattingNeeded: "",
    FormattedBy: "",
    Resume: null, // Placeholder for the resume file
  };
  const [formData, setFormData] = useState(initialFormData);
  const [validationErrors, setValidationErrors] = useState({});
  const [showPopup, setShowPopup] = useState(false);

  const [isSubmitting, setIsSubmitting] = useState(false);
  const resumeInputRef = useRef(null);

  const isValidEmail = (email) => {
    return /\S+@\S+\.\S+/.test(email);
  };

  const handleInputChange = (e) => {
    const { id, value, files } = e.target;
    if (id === "Resume" && files) {
      const file = files[0];
      if (file) {
        const allowedTypes = [
          "application/pdf",
          "application/msword",
          "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
        ];
        if (!allowedTypes.includes(file.type) || file.size > 1024 * 1024 * 10) {
          setValidationErrors({
            ...validationErrors,
            Resume: "Only PDF or DOC files under 10MB are allowed.",
          });
        } else {
          setFormData({
            ...formData,
            Resume: file,
          });
          let newErrors = { ...validationErrors };
          delete newErrors.Resume;
          setValidationErrors(newErrors);
        }
      }
    } else {
      setFormData({
        ...formData,
        [id]: value,
      });
      let newErrors = { ...validationErrors };
      delete newErrors[id];
      setValidationErrors(newErrors);
    }
  };

  const validateForm = () => {
    let isValid = true;
    const newErrors = {};
    Object.keys(formData).forEach((key) => {
      if (!formData[key] && key !== "Resume") {
        newErrors[key] = "This field is required.";
        isValid = false;
      }
    });
    if (formData.EmailId && !isValidEmail(formData.EmailId)) {
      newErrors.EmailId = "Invalid email format.";
      isValid = false;
    }

    if (!formData.Resume) {
      newErrors.Resume = "Please upload a resume.";
      isValid = false;
    }

    setValidationErrors(newErrors);
    return isValid;
  };
  const uploadResume = async (file) => {
    const filename = encodeURIComponent(file.name);
    // const uploadUrl = `https://lrl0r0t06c.execute-api.us-east-1.amazonaws.com/Prod/upload/${filename}`;
    const uploadUrl = `${process.env.REACT_APP_API_URL}/${filename}`;
    const fileData = new FormData();
    fileData.append("file", file);

    try {
      const response = await fetch(uploadUrl, {
        method: "POST",
        body: fileData,
      });
      const data = await response.json();

      console.log("Server response:", data);

      if (!response.ok) {
        throw new Error(`Server responded with status: ${response.status}`);
      }

      // Since the server confirms success but doesn't provide a URL, log the success and maybe return a success message instead
      if (data.message === "File uploaded successfully") {
        console.log(data.message);
        // Return a static message or a mock URL, or adjust as needed
        return "Upload successful, no URL provided"; // Placeholder return value
      } else {
        throw new Error(
          "Unexpected server response, upload may not be successful."
        );
      }
    } catch (error) {
      console.error("Upload Error:", error);
      throw error;
    }
  };

  //Dynamo DB API Call

  const submitForm = async () => {
    // Assuming you have an API endpoint to submit form data to DynamoDB
    const submitUrl =
      "https://mkou1u2f62.execute-api.us-east-1.amazonaws.com/Prod/submit";
    try {
      const response = await fetch(submitUrl, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) throw new Error("Failed to submit form.");
      return await response.json(); // or handle success without expecting a return value
    } catch (error) {
      console.error("Submission error:", error);
      throw error;
    }
  };

  //Working Submit Function for storing resume in S3 bucket
  // const handleSubmit = async (e) => {
  //   e.preventDefault();

  //   if (!validateForm()) {
  //     console.log("Form validation failed", validationErrors);
  //     return;
  //   }

  //   if (formData.Resume) {
  //     try {
  //       // Note: `uploadResult` now may not be a URL but a success message
  //       const uploadResult = await uploadResume(formData.Resume);
  //       console.log(uploadResult); // Log the success message or handle as needed

  //       // Proceed with the rest of your form submission logic
  //       console.log("Form Data:", JSON.stringify(formData, null, 2));
  //       setShowPopup(true);
  //     } catch (error) {
  //       alert("Failed to upload resume: " + error.message);
  //     }
  //   } else {
  //     alert("Please select a resume to upload.");
  //   }
  // };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    setIsSubmitting(true);

    try {
      if (formData.Resume) {
        const resumeUrl = await uploadResume(formData.Resume);
        // Update formData with the resume URL for submission
        const updatedFormData = { ...formData, ResumeUrl: resumeUrl };
        await submitForm(updatedFormData);
        setShowPopup(true); // Show success message
        setFormData(initialFormData); // Reset form
      } else {
        // Handle the case where a resume is required but not uploaded
        throw new Error("Resume is required.");
      }
    } catch (error) {
      alert(`Error: ${error.message}`);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleClosePopup = () => {
    setShowPopup(false);
    setFormData(initialFormData);
    setValidationErrors({});
    if (resumeInputRef.current) {
      resumeInputRef.current.value = "";
    }
  };

  return (
    <div>
      {/* The form component with Tailwind CSS */}
      <section className="max-w-4xl p-6 mx-auto bg-indigo-600 rounded-md shadow-md dark:bg-gray-800 mt-20">
        <h1 className="text-xl font-bold text-white capitalize dark:text-white">
          Employee Information Form
        </h1>
        <form onSubmit={handleSubmit}>
          <div className="grid grid-cols-1 gap-6 mt-4 sm:grid-cols-2">
            {/* Input fields generated based on formData */}
            {/* {Object.keys(initialFormData)
              .filter((key) => key !== "Resume")
              .map((key) => (
                <div key={key} className="mb-4">
                  <label
                    htmlFor={key}
                    className="text-white dark:text-gray-200 block text-sm font-bold mb-2"
                  >
                    {key.replace(/([A-Z])/g, " $1").trim()}
                  </label>
                  <input
                    // type="text"
                    type={key === "RequiredID" ? "number" : "String"} // Conditionally set the input type
                    id={key}
                    name={key}
                    value={formData[key]}
                    onChange={handleInputChange}
                    className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
                  />
                  {validationErrors[key] && (
                    <p className="text-red-500 text-xs italic">
                      {validationErrors[key]}
                    </p>
                  )}
                </div>
              ))} */}
            <div>
              <label
                className="text-white dark:text-gray-200"
                htmlFor="FullName"
              >
                Full Name
              </label>
              <input
                id="FullName"
                type="String"
                placeholder="Full Name"
                className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
                onChange={handleInputChange} // attach handleInputChange here
                value={formData.FullName}
              />
              {validationErrors.FullName && (
                <p className="text-red-500 text-xs italic">
                  {validationErrors.FullName}
                </p>
              )}
            </div>
            <div>
              <label className="text-white dark:text-gray-200" htmlFor="ReqId">
                Required ID
              </label>
              <input
                id="ReqId"
                type="number"
                placeholder="ID"
                className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
                onChange={handleInputChange} // attach handleInputChange here
                value={formData.ReqId}
              />
              {validationErrors.ReqId && (
                <p className="text-red-500 text-xs italic">
                  {validationErrors.ReqId}
                </p>
              )}
            </div>
            <div>
              <label
                className="text-white dark:text-gray-200"
                htmlFor="ReqTitle"
              >
                Required Title
              </label>
              <input
                id="ReqTitle"
                type="String"
                placeholder="Title"
                className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
                onChange={handleInputChange} // attach handleInputChange here
                value={formData.ReqTitle}
              />
              {validationErrors.ReqTitle && (
                <p className="text-red-500 text-xs italic">
                  {validationErrors.ReqTitle}
                </p>
              )}
            </div>
            <div>
              <label className="text-white dark:text-gray-200" htmlFor="Role">
                Role
              </label>
              <input
                id="Role"
                type="String"
                placeholder="Role"
                className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
                onChange={handleInputChange} // attach handleInputChange here
                value={formData.Role}
              />
              {validationErrors.Role && (
                <p className="text-red-500 text-xs italic">
                  {validationErrors.Role}
                </p>
              )}
            </div>
            <div>
              <label
                className="text-white dark:text-gray-200"
                htmlFor="ReqCreationDate"
              >
                Required Creation Date
              </label>
              <input
                id="ReqCreationDate"
                // type="Number"
                type="String"
                placeholder="MM/DD/YYYY"
                className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
                onChange={handleInputChange} // attach handleInputChange here
                value={formData.ReqCreationDate}
              />
              {validationErrors.ReqCreationDate && (
                <p className="text-red-500 text-xs italic">
                  {validationErrors.ReqCreationDate}
                </p>
              )}
            </div>
            <div>
              <label
                className="text-white dark:text-gray-200"
                htmlFor="VendorID"
              >
                Vendor ID
              </label>
              <input
                id="VendorID"
                type="String"
                placeholder="Vendor ID"
                className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
                onChange={handleInputChange} // attach handleInputChange here
                value={formData.VendorID}
              />
              {validationErrors.VendorID && (
                <p className="text-red-500 text-xs italic">
                  {validationErrors.VendorID}
                </p>
              )}
            </div>
            <div>
              <label
                className="text-white dark:text-gray-200"
                htmlFor="ImmigrationStatus"
              >
                Immigration Status
              </label>
              <input
                id="ImmigrationStatus"
                type="String"
                placeholder="Immigration Status"
                className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
                onChange={handleInputChange} // attach handleInputChange here
                value={formData.ImmigrationStatus}
              />
              {validationErrors.ImmigrationStatus && (
                <p className="text-red-500 text-xs italic">
                  {validationErrors.ImmigrationStatus}
                </p>
              )}
            </div>
            <div>
              <label
                className="text-white dark:text-gray-200"
                htmlFor="ContractType"
              >
                Contract Type
              </label>
              <input
                id="ContractType"
                type="String"
                placeholder="Contract Type"
                className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
                onChange={handleInputChange} // attach handleInputChange here
                value={formData.ContractType}
              />
              {validationErrors.ContractType && (
                <p className="text-red-500 text-xs italic">
                  {validationErrors.ContractType}
                </p>
              )}
            </div>
            <div>
              <label
                className="text-white dark:text-gray-200"
                htmlFor="SubmissionDate"
