  //       method: "POST",
  //       body: fileData,
  //     });
  //     const data = await response.json();
  //     if (!response.ok || !data.url) {
  //       throw new Error("Failed to upload resume");
  //     }
  //     return data.url; // Return the URL of the uploaded file
  //   } catch (error) {
  //     console.error("Upload Error:", error);
  //     throw error;
  //   }
  // };

  //2 - Test -1
  // const uploadResume = async (file) => {
  //   const filename = encodeURIComponent(file.name);
  //   const uploadUrl = `https://lrl0r0t06c.execute-api.us-east-1.amazonaws.com/Prod/upload/${filename}`;
  //   const fileData = new FormData();
  //   fileData.append("file", file); // Ensure your server expects the file under this key

  //   try {
  //     const response = await fetch(uploadUrl, {
  //       method: "POST",
  //       body: fileData,
  //       // Add any necessary headers for your API, like API keys or content type if needed.
  //       // headers: {
  //       //   'Content-Type': 'multipart/form-data', // Note: Including this header can cause issues with fetch in some cases as it doesn't allow setting boundary parameter
  //       // },
  //     });

  //     const data = await response.json();

  //     // Log the full response for debugging
  //     console.log("Full server response:", data);

  //     if (!response.ok) {
  //       throw new Error(`Server responded with status: ${response.status}`);
  //     }

  //     // Check for a URL in the response, or adjust according to your actual API response structure
  //     if (data.url) {
  //       return data.url;
  //     } else {
  //       // Log the missing URL issue and throw an error
  //       console.error("No URL in response:", data);
  //       throw new Error("No URL returned from upload API");
  //     }
  //   } catch (error) {
  //     console.error("Upload Error:", error);
  //     throw error; // Rethrow the error to be handled in handleSubmit
  //   }
  // };

  //3 - Test -2
  const uploadResume = async (file) => {
    const filename = encodeURIComponent(file.name);
    const uploadUrl = `https://lrl0r0t06c.execute-api.us-east-1.amazonaws.com/Prod/upload/${filename}`;
    const fileData = new FormData();
    fileData.append("file", file);

    try {
      const response = await fetch(uploadUrl, {
        method: "POST",
        body: fileData,
      });
      const data = await response.json();

      console.log("Server response:", data);

      if (!response.ok) {
        throw new Error(`Server responded with status: ${response.status}`);
      }

      // Since the server confirms success but doesn't provide a URL, log the success and maybe return a success message instead
      if (data.message === "File uploaded successfully") {
        console.log(data.message);
        // Return a static message or a mock URL, or adjust as needed
        return "Upload successful, no URL provided"; // Placeholder return value
      } else {
        throw new Error(
          "Unexpected server response, upload may not be successful."
        );
      }
    } catch (error) {
      console.error("Upload Error:", error);
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      console.log("Form validation failed", validationErrors);
      return;
    }

    if (formData.Resume) {
      try {
        // Note: `uploadResult` now may not be a URL but a success message
        const uploadResult = await uploadResume(formData.Resume);
        console.log(uploadResult); // Log the success message or handle as needed

        // Proceed with the rest of your form submission logic
        console.log("Form Data:", JSON.stringify(formData, null, 2));
        setShowPopup(true);
      } catch (error) {
        alert("Failed to upload resume: " + error.message);
      }
    } else {
      alert("Please select a resume to upload.");
    }
  };

  //1 - Working
