import React, { useState } from "react";
import authenticateUser from "./authenticateUser";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");
  const [isChangingPassword, setIsChangingPassword] = useState(false);

  const handleSignIn = async (event) => {
    event.preventDefault();
    try {
      const response = await authenticateUser(username, password, newPassword);
      if (response.status === "newPasswordRequired") {
        setUser(response.user);
        setIsChangingPassword(true);
      } else if (response.status === "success") {
        console.log("Login successful!", response.data);
        // Handle successful login
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleNewPassword = async (event) => {
    event.preventDefault();
    try {
      const response = await authenticateUser(username, password, newPassword);
      if (response.status === "success") {
        console.log("Password changed and login successful!", response.data);
        // Handle successful password change and login
      }
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div>
      {!isChangingPassword ? (
        // Regular login form
        <form onSubmit={handleSignIn}>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
          />
          <button type="submit">Login</button>
          {error && <p>{error}</p>}
        </form>
      ) : (
        // New password form
        <form onSubmit={handleNewPassword}>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            placeholder="New Password"
          />
          <button type="submit">Set New Password</button>
          {error && <p>{error}</p>}
        </form>
      )}
    </div>
  );
};

export default Login;
