import React, { useState, useRef } from "react";

function SuccessPage() {
  const initialFormData = {
    FullName: "",
    ReqId: "",
    ReqTitle: "",
    Role: "",
    ReqCreationDate: "",
    VendorID: "",
    BillRate: "",
    ImmigrationStatus: "",
    ContractType: "",
    SubmissionDate: "",
    ReqSubmissionEndDate: "",
    CandidateCurrentLocation: "",
    ContactNumber: "",
    RecruiterName: "",
    State: "",
    SubmissionStatus: "",
    VendorRate: "",
    CandidatePayRate: "",
    BillRateMargin: "",
    ResumeSource: "",
    EmailId: "",
    LinkedInID: "",
    EmployerInformation: "",
    ProfessionalReferences: "",
    DOB: "",
    ReqSkills: "",
    ResumeFormattingNeeded: "",
    FormattedBy: "",
    Resume: null, // Placeholder for the resume file
  };

  const [formData, setFormData] = useState(initialFormData);
  const [validationErrors, setValidationErrors] = useState({});
  const [showPopup, setShowPopup] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const resumeInputRef = useRef(null);

  const formFieldsConfig = [
    { id: "FullName", type: "text", placeholder: "Full Name" },
    { id: "ReqId", type: "number", placeholder: "Required ID" },
    { id: "ReqTitle", type: "text", placeholder: "Required Title" },
    { id: "Role", type: "text", placeholder: "Role" },
    { id: "ReqCreationDate", type: "date", placeholder: "MM/DD/YYYY" },
    { id: "VendorID", type: "text", placeholder: "Vendor ID" },
    { id: "BillRate", type: "number", placeholder: "Bill Rate" },
    {
      id: "ImmigrationStatus",
      type: "text",
      placeholder: "Immigration Status",
    },
    { id: "ContractType", type: "text", placeholder: "Contract Type" },
    { id: "SubmissionDate", type: "date", placeholder: "MM/DD/YYYY" },
    { id: "ReqSubmissionEndDate", type: "date", placeholder: "MM/DD/YYYY" },
    {
      id: "CandidateCurrentLocation",
      type: "text",
      placeholder: "Candidate Current Location",
    },
    { id: "ContactNumber", type: "tel", placeholder: "Contact Number" },
    { id: "RecruiterName", type: "text", placeholder: "Recruiter Name" },
    { id: "State", type: "text", placeholder: "State" },
    { id: "SubmissionStatus", type: "text", placeholder: "Submission Status" },
    { id: "VendorRate", type: "number", placeholder: "Vendor Rate" },
    {
      id: "CandidatePayRate",
      type: "number",
      placeholder: "Candidate Pay Rate",
    },
    { id: "BillRateMargin", type: "number", placeholder: "Bill Rate Margin" },
    { id: "ResumeSource", type: "text", placeholder: "Resume Source" },
    { id: "EmailId", type: "email", placeholder: "Email ID" },
    { id: "LinkedInID", type: "text", placeholder: "LinkedIn ID" },
    {
      id: "EmployerInformation",
      type: "text",
      placeholder: "Employer Information",
    },
    {
      id: "ProfessionalReferences",
      type: "text",
      placeholder: "Professional References",
    },
    { id: "DOB", type: "date", placeholder: "DOB" },
    { id: "ReqSkills", type: "text", placeholder: "Required Skills" },
    {
      id: "ResumeFormattingNeeded",
      type: "text",
      placeholder: "Resume Formatting Needed?",
    },
    { id: "FormattedBy", type: "text", placeholder: "Formatted By" },
  ];

  const handleInputChange = (e) => {
    const { id, value, files } = e.target;
    if (id === "Resume" && files) {
      const file = files[0];
      if (file) {
        const allowedTypes = [
          "application/pdf",
          "application/msword",
          "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
        ];
        if (!allowedTypes.includes(file.type) || file.size > 1024 * 1024 * 10) {
          setValidationErrors((prevErrors) => ({
            ...prevErrors,
            Resume: "Only PDF or DOC files under 10MB are allowed.",
          }));
        } else {
          setFormData((prevFormData) => ({ ...prevFormData, Resume: file }));
          setValidationErrors((prevErrors) => {
            const newErrors = { ...prevErrors };
            delete newErrors.Resume;
            return newErrors;
          });
        }
      }
    } else {
      setFormData((prevFormData) => ({ ...prevFormData, [id]: value }));
      setValidationErrors((prevErrors) => {
        const newErrors = { ...prevErrors };
        delete newErrors[id];
        return newErrors;
      });
    }
  };

  const validateForm = () => {
    let isValid = true;
    const newErrors = {};
    Object.keys(formData).forEach((key) => {
      if (!formData[key] && key !== "Resume") {
        newErrors[key] = "This field is required.";
        isValid = false;
      }
    });

    if (formData.EmailId && !isValidEmail(formData.EmailId)) {
      newErrors.EmailId = "Invalid email format.";
      isValid = false;
    }

    if (!formData.Resume) {
      newErrors.Resume = "Please upload a resume.";
      isValid = false;
    }

    setValidationErrors(newErrors);
    return isValid;
  };

  const uploadResume = async (file) => {
    // Placeholder for your upload logic
    const filename = encodeURIComponent(file.name);
    const uploadUrl = `${process.env.REACT_APP_API_URL}/upload/${filename}`;
    const fileData = new FormData();
    fileData.append("file", file);

    try {
      const response = await fetch(uploadUrl, {
        method: "POST",
        body: fileData,
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(`Server responded with status: ${response.status}`);
      }
      console.log("Server response:", data.message);
      // Assuming the backend response contains the URL or path of the uploaded file
      return data.url || "Upload successful, URL not provided";
    } catch (error) {
      console.error("Upload Error:", error);
      throw error;
    }
  };

  const submitForm = async () => {
    // Placeholder for your submission logic
    const submitUrl =
      "https://mkou1u2f62.execute-api.us-east-1.amazonaws.com/Prod/submit";
    try {
      const response = await fetch(submitUrl, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      if (!response.ok) throw new Error("Failed to submit form.");
      const data = await response.json();
      return data;
    } catch (error) {
      console.error("Submission error:", error);
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Placeholder for your handle submit logic
    if (!validateForm()) return;
    setIsSubmitting(true);

    try {
      let resumeUrl = "";
      if (formData.Resume) {
        resumeUrl = await uploadResume(formData.Resume);
      }
      const updatedFormData = { ...formData, ResumeUrl: resumeUrl };
      await submitForm(updatedFormData);
      setShowPopup(true);
      setFormData(initialFormData);
    } catch (error) {
      alert(`Error: ${error.message}`);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleClosePopup = () => {
    // Placeholder for your close popup logic
    setShowPopup(false);
    setFormData(initialFormData);
    setValidationErrors({});
    if (resumeInputRef.current) {
      resumeInputRef.current.value = "";
    }
  };

  const renderFormFields = () => {
    return formFieldsConfig.map(({ id, type, placeholder }) => (
      <div key={id} className="mb-4">
        <label
          htmlFor={id}
          className="text-white dark:text-gray-200 block text-sm font-bold mb-2"
        >
          {placeholder}
        </label>
        <input
          id={id}
          type={type}
          placeholder={placeholder}
          value={formData[id]}
          onChange={handleInputChange}
          className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-gray-300 rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
        />
        {validationErrors[id] && (
          <p className="text-red-500 text-xs italic">{validationErrors[id]}</p>
        )}
      </div>
    ));
  };

  return <div>{/* Your form and popup logic here */}</div>;
}

export default SuccessPage;
