import React, { useState, useRef } from "react";

function SuccessPage() {
  // Initial state for form data, including a placeholder for the resume file
  const initialFormData = {
    FullName: "",
    RequiredID: "",
    RequiredTitle: "",
    Role: "",
    RequiredCreationDate: "",
    VendorID: "",
    ImmigrationStatus: "",
    ContractType: "",
    SubmissionDate: "",
    RequiredSubmissionEndDate: "",
    CandidateCurrentLocation: "",
    ContactNumber: "",
    RecruiterName: "",
    State: "",
    SubmissionStatus: "",
    VendorRate: "",
    CandidatePayRate: "",
    BillRateMargin: "",
    ResumeSource: "",
    EmailId: "",
    LinkedInID: "",
    EmployerInformation: "",
    ProfessionalReferences: "",
    DOB: "",
    RequiredSkills: "",
    ResumeFormattingNeeded: "",
    FormattedBy: "",
    Resume: null, // Placeholder for the resume file
  };

  // State hooks for form data, validation errors, and submission status
  const [formData, setFormData] = useState(initialFormData);
  const [validationErrors, setValidationErrors] = useState({});
  const [showPopup, setShowPopup] = useState(false);
  const resumeInputRef = useRef(null); // Ref for the resume input to reset it upon submission

  // Handles form input changes and updates state accordingly
  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    // Reset validation errors for the current input
    setValidationErrors((prevErrors) => ({ ...prevErrors, [name]: null }));

    if (name === "Resume" && files) {
      setFormData((prevFormData) => ({ ...prevFormData, Resume: files[0] }));
    } else {
      setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
    }
  };

  // Function to validate the entire form before submission
  const validateForm = () => {
    const newErrors = {};
    Object.keys(formData).forEach((key) => {
      // Check for empty fields, excluding the Resume for separate validation
      if (!formData[key] && key !== "Resume") {
        newErrors[key] = "This field is required.";
      }
    });

    // Validate the resume file, if selected
    if (formData.Resume) {
      const { Resume } = formData;
      const allowedTypes = [
        "application/pdf",
        "application/msword",
        "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
      ];
      const maxSize = 10485760; // 10MB

      if (!allowedTypes.includes(Resume.type) || Resume.size > maxSize) {
        newErrors.Resume = "Only PDF or DOC files under 10MB are allowed.";
      }
    } else {
      // Ensure a file is selected
      newErrors.Resume = "Please upload a resume.";
    }

    setValidationErrors(newErrors);
    // Form is valid if no errors were added
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission with validation and data processing
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log("Form Data:", formData);
      // Implement the upload logic here
      setShowPopup(true); // Show success message on successful validation/submission
    } else {
      console.log("Validation errors:", validationErrors);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {Object.keys(initialFormData).map((key) => (
          <div key={key} className="mb-4">
            <label htmlFor={key}>
              {key.replace(/([A-Z])/g, " $1").trim()}:
            </label>
            {key === "Resume" ? (
              <input
                type="file"
                name="key"
                onChange={handleInputChange}
                ref={resumeInputRef}
                className={validationErrors[key] ? "border-red-500" : ""}
                required
              />
            ) : (
              <input
                type="text"
                name={key}
                value={formData[key]}
                onChange={handleInputChange}
                className={validationErrors[key] ? "border-red-500" : ""}
                required
              />
            )}
            {validationErrors[key] && (
              <div className="text-red-500">{validationErrors[key]}</div>
            )}
          </div>
        ))}
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          Submit
        </button>
      </form>
      {showPopup && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex justify-center items-center">
          <div className="bg-white p-5 rounded-lg shadow-lg text-center">
            <h2 className="text-lg mb-4">Submission Successful</h2>
            <p>Your form has been submitted successfully.</p>
            <pre>{JSON.stringify(formData, null, 2)}</pre>
            <button
              onClick={() => setShowPopup(false)}
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mt-4"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default SuccessPage;
